import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const IngredientList = () => {
  const [ingredients, setIngredients] = useState([]);
  const [filter, setFilter] = useState({ category: "", storage_location: "" });
  const [categories, setCategories] = useState([]);
  const [storageLocations, setStorageLocations] = useState([]);

  useEffect(() => {
    fetchIngredients();
  }, []);

  const fetchIngredients = async () => {
    try {
      const { data } = await axios.get("http://127.0.0.1:8000/ingredients");
      setIngredients(data.ingredients);

      // Get unique categories & storage locations
      const uniqueCategories = [...new Set(data.ingredients.map((item) => item.item_category))];
      const uniqueStorage = [...new Set(data.ingredients.map((item) => item.storage_location))];

      setCategories(uniqueCategories);
      setStorageLocations(uniqueStorage);
    } catch (error) {
      console.error("Error fetching ingredients:", error);
    }
  };

  const updateStock = async (barcode, amount, type) => {
    try {
      if (amount <= 0) return;

      await axios.patch(`http://127.0.0.1:8000/${type}/${barcode}`, { amount });

      // Update UI **without moving rows**
      setIngredients((prev) =>
        prev.map((item) =>
          item.barcode === barcode ? { ...item, num_containers: item.num_containers + (type === "increase" ? amount : -amount) } : item
        )
      );
    } catch (error) {
      console.error("Error updating stock:", error);
    }
  };

  const exportToCSV = () => {
    const csvContent = [
      ["Name", "Brand", "Quantity", "Container", "Storage Location"],
      ...ingredients.map(({ ingredient_name, brand, num_containers, container_type, storage_location }) => [
        ingredient_name,
        brand,
        num_containers,
        container_type,
        storage_location,
      ]),
    ]
      .map((row) => row.join(","))
      .join("\n");

    const blob = new Blob([csvContent], { type: "text/csv" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "inventory.csv";
    link.click();
  };

  const handleFilterChange = (e) => {
    setFilter({ ...filter, [e.target.name]: e.target.value });
  };

  const filteredIngredients = ingredients.filter((item) => {
    return (
      (filter.category ? item.item_category === filter.category : true) &&
      (filter.storage_location ? item.storage_location === filter.storage_location : true)
    );
  });

  return (
    <div>
      {/* üèÜ Tabs Moved to the Top */}
      <div className="nav-container">
        <div className="nav-tabs">
          <button className="tab-btn active">Inventory</button>
          <button className="tab-btn">Expiration</button>
        </div>
        <button className="export-btn" onClick={exportToCSV}>
          Export to CSV
        </button>
      </div>

      {/* üèÜ Filters Dynamically Populated */}
      <div className="filter-container">
        <select name="category" onChange={handleFilterChange}>
          <option value="">All Categories</option>
          {categories.map((cat) => (
            <option key={cat} value={cat}>
              {cat}
            </option>
          ))}
        </select>

        <select name="storage_location" onChange={handleFilterChange}>
          <option value="">All Storage</option>
          {storageLocations.map((loc) => (
            <option key={loc} value={loc}>
              {loc}
            </option>
          ))}
        </select>
      </div>

      {/* üèÜ Table Order Fixed */}
      <table className="inventory-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Brand</th>
            <th>Quantity</th>
            <th>Container</th>
            <th>Storage Location</th>
            <th>Actions</th>
            <th>Edit</th>
          </tr>
        </thead>
        <tbody>
          {filteredIngredients.map((item) => (
            <tr key={item.barcode}>
              <td>{item.ingredient_name}</td>
              <td>{item.brand}</td>
              <td>{item.num_containers}</td>
              <td>{item.container_type}</td>
              <td>{item.storage_location}</td>
              <td>
                <input type="number" min="1" placeholder="Qty" id={`qty-${item.barcode}`} />
                <button onClick={() => updateStock(item.barcode, Number(document.getElementById(`qty-${item.barcode}`).value), "increase")}>+</button>
                <button onClick={() => updateStock(item.barcode, Number(document.getElementById(`qty-${item.barcode}`).value), "decrease")}>-</button>
              </td>
              <td>
                <Link to={`/ingredient/${item.barcode}`}>Edit</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default IngredientList;
