import React, { useEffect, useState } from "react";
import axios from "axios";

const GroceryListPage = () => {
  const [groceryList, setGroceryList] = useState([]);

  useEffect(() => {
    const fetchGroceryList = async () => {
      try {
        const res = await axios.get("http://127.0.0.1:8000/grocery_list");
        setGroceryList(res.data.grocery_list || []); // ✅ fallback to empty array
      } catch (err) {
        console.error("Error fetching grocery list:", err);
        setGroceryList([]); // ✅ ensure state is never undefined
      }
    };
    fetchGroceryList();
  }, []);

  return (
    <div className="grocery-list-tab">
      <h2>Grocery List</h2>

      {groceryList.length === 0 ? (
        <p>No items in the grocery list.</p>
      ) : (
        <table>
          <thead>
            <tr>
              <th>Item</th>
              <th>Qty</th>
            </tr>
          </thead>
          <tbody>
            {groceryList.map((g, idx) => (
              <tr key={idx}>
                <td>{g.item}</td>
                <td>{g.qty}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default GroceryListPage;
