import React, { useState } from "react";
import { QueryClient, QueryClientProvider } from "react-query";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import IngredientList from "./components/IngredientList";
import AddRemoveIngredient from "./components/AddRemoveIngredient";
import SearchBar from "./components/SearchBar";
import IngredientDetails from "./components/IngredientDetails";

const queryClient = new QueryClient();

function App() {
    const [searchQuery, setSearchQuery] = useState("");
    const [activeTab, setActiveTab] = useState("inventory");

    // Function to handle Export CSV (Placeholder, Implement Backend Logic)
    const handleExportCSV = () => {
        alert("Exporting CSV... (Implement Backend Logic)");
    };

    return (
        <QueryClientProvider client={queryClient}>
            <Router>
                {/* Header */}
                <header className="header">
                    <h1>Neighborhood Meals on Wheels Inventory Application</h1>
                    <button className="export-btn" onClick={handleExportCSV}>Export to CSV</button>
                </header>

                {/* Navigation Tabs */}
                <nav className="tabs">
                    <Link 
                        to="/" 
                        className={activeTab === "inventory" ? "active" : ""}
                        onClick={() => setActiveTab("inventory")}
                    >
                        Inventory
                    </Link>
                    <Link 
                        to="/expiration"
                        className={activeTab === "expiration" ? "active" : ""}
                        onClick={() => setActiveTab("expiration")}
                    >
                        Expiration
                    </Link>
                </nav>

                {/* Main Content */}
                <div className="p-6">
                    <SearchBar setSearchQuery={setSearchQuery} />
                    <Routes>
                        <Route
                            path="/"
                            element={
                                <>
                                    <AddRemoveIngredient />
                                    <IngredientList searchQuery={searchQuery} />
                                </>
                            }
                        />
                        <Route path="/ingredient/:barcode" element={<IngredientDetails />} />
                    </Routes>
                </div>
            </Router>
        </QueryClientProvider>
    );
}

export default App;
