import React, { useState, useEffect } from "react";
import axios from "axios";
import "./MenuSubstitutions.css";

const weekdays = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"];

const MenuSubstitutions = () => {
  const [menu, setMenu] = useState({});
  const [mealsPerDay, setMealsPerDay] = useState({});
  const [ingredients, setIngredients] = useState([]);

  // Fetch ingredients from database
  useEffect(() => {
    const fetchIngredients = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/ingredients");
        if (response.data.ingredients && Array.isArray(response.data.ingredients)) {
          setIngredients(response.data.ingredients);
        } else {
          console.error("Invalid data format:", response.data);
        }
      } catch (error) {
        console.error("Error fetching ingredients:", error);
      }
    };
    fetchIngredients();
  }, []);

  // Format ingredients for the dropdown
  const ingredientOptions = ingredients.map((ingredient) => ({
    value: ingredient.ingredient_name,
    label: ingredient.ingredient_name,
    category: ingredient.item_category, // Used later for substitutions
  }));

  // Handle selecting ingredients for each meal
  const handleSelect = (day, mealType, category, value) => {
    setMenu((prevMenu) => ({
      ...prevMenu,
      [day]: {
        ...prevMenu[day],
        [mealType]: {
          ...prevMenu[day]?.[mealType],
          [category]: value || null,
        },
      },
    }));
  };

  // Handle updating # of meals per day
  const handleMealCountChange = (day, value) => {
    setMealsPerDay((prev) => ({
      ...prev,
      [day]: value,
    }));
  };

  return (
    <div className="menu-substitutions">
      <h2>Menu Substitutions</h2>
      <p>Select ingredients for each day's menu and check if they are feasible.</p>

      <div className="menu-container">
        {weekdays.map((day) => (
          <div key={day} className="day-column">
            <h3>{day}</h3>
            <label># of Meals:</label>
            <input
              type="number"
              min="1"
              value={mealsPerDay[day] || ""}
              onChange={(e) => handleMealCountChange(day, e.target.value)}
            />

            <h4>Hot Meal</h4>
            <label>Protein:</label>
            <select onChange={(e) => handleSelect(day, "hot", "protein", e.target.value)}>
              <option value="">Select an option</option>
              {ingredientOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>

            <label>Vegetable:</label>
            <select onChange={(e) => handleSelect(day, "hot", "vegetable", e.target.value)}>
              <option value="">Select an option</option>
              {ingredientOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>

            <label>Starch:</label>
            <select onChange={(e) => handleSelect(day, "hot", "starch", e.target.value)}>
              <option value="">Select an option</option>
              {ingredientOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>

            <h4>Cold Meal</h4>
            <label>Dessert:</label>
            <select onChange={(e) => handleSelect(day, "cold", "dessert", e.target.value)}>
              <option value="">Select an option</option>
              {ingredientOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>

            <label>Vegetable:</label>
            <select onChange={(e) => handleSelect(day, "cold", "vegetable", e.target.value)}>
              <option value="">Select an option</option>
              {ingredientOptions.map((option) => (
                <option key={option.value} value={option.value}>
                  {option.label}
                </option>
              ))}
            </select>
          </div>
        ))}
      </div>

      <button className="check-feasibility">Check Feasibility</button>

      <div className="substitutions-section">
        <h3>Substitution Suggestions</h3>
        <p>(This will display substitution recommendations once implemented.)</p>
      </div>
    </div>
  );
};

export default MenuSubstitutions;
