import React, { useState, useEffect } from "react";
import Select from "react-select";
import axios from "axios";
import "./MenuSubstitutions.css";

const weekdays = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday"];

const categoryFilters = {
  protein: ["Protein Entree", "Starch and Protein Entree", "Canned Entree"],
  hotSide: [
    "Vegetable (Dry & Freezer)",
    "Starch (Dry & Freezer)",
    "Beans",
    "Starch and Protein Entree",
    "Canned Entree",
  ],
  dessertSnack: ["Dessert", "Snack"],
  fruitSalad: [
    { category: "Fruit", storage: ["Dry", "Fridge"] },
    { category: "Vegetable", storage: ["Fridge"] },
  ],
  fruitSnack: [
    { category: "Fruit", storage: ["Dry", "Fridge"] },
    { category: "Snack" },
  ],
  sauce: ["Sauce"],
};

// Helper to filter ingredients by category and (optional) storage_type
const filterIngredients = (ingredients, filterSet) => {
  return ingredients.filter((item) => {
    // Handle structure with both category and storage filters
    if (Array.isArray(filterSet)) {
      return filterSet.some((f) => {
        if (typeof f === "string") {
          return item.item_category === f;
        } else {
          return (
            item.item_category === f.category &&
            (!f.storage || f.storage.includes(item.storage_type))
          );
        }
      });
    }
    return false;
  });
};

const MenuSubstitutions = () => {
  const [menu, setMenu] = useState({});
  const [mealsPerDay, setMealsPerDay] = useState({});
  const [ingredients, setIngredients] = useState([]);

  useEffect(() => {
    const fetchIngredients = async () => {
      try {
        const response = await axios.get("http://127.0.0.1:8000/ingredients");
        if (response.data.ingredients && Array.isArray(response.data.ingredients)) {
          setIngredients(response.data.ingredients);
        } else {
          console.error("Invalid data format:", response.data);
        }
      } catch (error) {
        console.error("Error fetching ingredients:", error);
      }
    };
    fetchIngredients();
  }, []);

  const handleSelect = (day, mealType, field, selectedOption) => {
    setMenu((prev) => ({
      ...prev,
      [day]: {
        ...prev[day],
        [mealType]: {
          ...prev[day]?.[mealType],
          [field]: selectedOption ? selectedOption.value : null,
        },
      },
    }));
  };

  const renderSelect = (day, mealType, field, label, options, isOptional = false) => (
    <div>
      <label>{label}{isOptional ? " (optional)" : ""}:</label>
      <Select
        isClearable={isOptional}
        options={options.map((i) => ({
          value: i.ingredient_name,
          label: i.ingredient_name,
        }))}
        value={
          menu[day]?.[mealType]?.[field]
            ? { value: menu[day][mealType][field], label: menu[day][mealType][field] }
            : null
        }
        onChange={(option) => handleSelect(day, mealType, field, option)}
        isSearchable
      />
    </div>
  );

  return (
    <div className="menu-substitutions">
      <h2>Menu Substitutions</h2>
      <p>Select ingredients for each day's menu and check if they are feasible.</p>

      <div className="menu-container">
        {weekdays.map((day) => (
          <div key={day} className="day-column">
            <h3>{day}</h3>
            <label># of Meals:</label>
            <input
              type="number"
              min="1"
              value={mealsPerDay[day] || ""}
              onChange={(e) =>
                setMealsPerDay({ ...mealsPerDay, [day]: e.target.value })
              }
            />

            <h4>Hot Tray</h4>
            {renderSelect(day, "hot", "protein", "Protein", filterIngredients(ingredients, categoryFilters.protein))}
            {renderSelect(day, "hot", "hotSide1", "Hot Side 1", filterIngredients(ingredients, categoryFilters.hotSide))}
            {renderSelect(day, "hot", "hotSide2", "Hot Side 2", filterIngredients(ingredients, categoryFilters.hotSide))}
            {renderSelect(day, "hot", "sauce", "Sauce", filterIngredients(ingredients, categoryFilters.sauce), true)}

            <h4>Cold Tray</h4>
            {renderSelect(day, "cold", "dessertSnack", "Dessert/Snack", filterIngredients(ingredients, categoryFilters.dessertSnack))}
            {renderSelect(day, "cold", "fruitSalad", "Fruit/Salad", filterIngredients(ingredients, categoryFilters.fruitSalad))}
            {renderSelect(day, "cold", "fruitSnack", "Fruit/Snack", filterIngredients(ingredients, categoryFilters.fruitSnack))}
            {renderSelect(day, "cold", "sauce", "Sauce", filterIngredients(ingredients, categoryFilters.sauce), true)}
          </div>
        ))}
      </div>

      <button className="check-feasibility">Check Feasibility</button>

      <div className="substitutions-section">
        <h3>Substitution Suggestions</h3>
        <p>(This will display substitution recommendations once implemented.)</p>
      </div>
    </div>
  );
};

export default MenuSubstitutions;
