import React from "react";
import { useQuery } from "react-query";
import { fetchIngredients } from "../supabaseClient";
import StockControls from "./StockControls";

const IngredientList = () => {
  const { data, error, isLoading } = useQuery("ingredients", fetchIngredients);

  if (isLoading) return <div>Loading...</div>;
  if (error) return <div>Error fetching ingredients</div>;

  return (
    <table className="w-full border-collapse mt-4">
      <thead>
        <tr className="bg-gray-200">
          <th className="border p-2">Barcode</th>
          <th className="border p-2">Name</th>
          <th className="border p-2">Quantity</th>
          <th className="border p-2">Actions</th>
        </tr>
      </thead>
      <tbody>
        {data.map((item) => (
          <tr key={item.barcode} className="border">
            <td className="border p-2">{item.barcode}</td>
            <td className="border p-2">{item.ingredient_name}</td>
            <td className="border p-2">{item.num_containers}</td>
            <td className="border p-2">
              <StockControls barcode={item.barcode} />
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};

export default IngredientList;
