import React, { useState } from "react";
import { QueryClient, QueryClientProvider } from "react-query";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import IngredientList from "./components/IngredientList";
import AddRemoveIngredient from "./components/AddRemoveIngredient";
import SearchBar from "./components/SearchBar";
import IngredientDetails from "./components/IngredientDetails";
import "./index.css";

const queryClient = new QueryClient();

function App() {
  const [searchQuery, setSearchQuery] = useState("");
  const [activeTab, setActiveTab] = useState("inventory");

  const handleExportCSV = () => {
    const csvData = [
      ["Name", "Brand", "Quantity", "Container", "Storage Location"],
      ["Carrots", "Stokely", 8, "Cans", "E3"],
      ["Green beans", "Chef's Quality", 8, "Cans", "E3"],
    ];

    let csvContent =
      "data:text/csv;charset=utf-8," +
      csvData.map((e) => e.join(",")).join("\n");
    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", "inventory.csv");
    document.body.appendChild(link);
    link.click();
  };

  return (
    <QueryClientProvider client={queryClient}>
      <Router>
        {/* Navbar */}
        <div className="navbar">
          Neighborhood Meals on Wheels Inventory Application
        </div>

        {/* Tabs */}
        <div className="tabs-container">
          <button
            className={`tab-button ${activeTab === "inventory" ? "active" : ""}`}
            onClick={() => setActiveTab("inventory")}
          >
            Inventory
          </button>
          <button
            className={`tab-button ${activeTab === "expiration" ? "active" : ""}`}
            onClick={() => setActiveTab("expiration")}
          >
            Expiration
          </button>
        </div>

        {/* Search and Export */}
        <div className="search-export-container">
          <SearchBar setSearchQuery={setSearchQuery} />
          <button className="export-btn" onClick={handleExportCSV}>
            Export to CSV
          </button>
        </div>

        {/* Main Content */}
        <div className="p-6">
          <Routes>
            <Route
              path="/"
              element={
                <>
                  <AddRemoveIngredient />
                  <IngredientList searchQuery={searchQuery} />
                </>
              }
            />
            <Route path="/ingredient/:barcode" element={<IngredientDetails />} />
          </Routes>
        </div>
      </Router>
    </QueryClientProvider>
  );
}

export default App;
