import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const IngredientDetails = () => {
  const { barcode } = useParams();
  const [ingredient, setIngredient] = useState(null);

  useEffect(() => {
    const fetchIngredient = async () => {
      try {
        const response = await axios.get(`http://127.0.0.1:8000/ingredients`);
        const found = response.data.ingredients.find((item) => item.barcode === barcode);
        setIngredient(found);
      } catch (error) {
        console.error("Error fetching ingredient details", error);
      }
    };

    fetchIngredient();
  }, [barcode]);

  if (!ingredient) return <p>Loading ingredient details...</p>;

  return (
    <div>
      <h2>{ingredient.ingredient_name} Details</h2>
      <p><strong>Barcode:</strong> {ingredient.barcode}</p>
      <p><strong>Quantity:</strong> {ingredient.num_containers}</p>
      <p><strong>Unit:</strong> {ingredient.unit}</p>
      <p><strong>Expiration Date:</strong> {ingredient.expiration_date}</p>
      <p><strong>Storage Location:</strong> {ingredient.storage_location}</p>
      <p><strong>Item Category:</strong> {ingredient.item_category}</p>
      <p><strong>Storage Type:</strong> {ingredient.storage_type}</p>
      <p><strong>Container Type:</strong> {ingredient.container_type}</p>
      <p><strong>Brand:</strong> {ingredient.brand}</p>
      <p><strong>TEFAP:</strong> {ingredient.tefap ? "Yes" : "No"}</p>
    </div>
  );
};

export default IngredientDetails;
