import React, { useState } from "react";
import { useQuery, useMutation, useQueryClient } from "react-query";
import { Link } from "react-router-dom";
import axios from "axios";

const fetchIngredients = async () => {
  const { data } = await axios.get("http://127.0.0.1:8000/ingredients");
  return data.ingredients;
};

const IngredientList = () => {
  const queryClient = useQueryClient();
  const { data: ingredients, error, isLoading } = useQuery("ingredients", fetchIngredients);
  const [quantities, setQuantities] = useState({});

  const updateStock = useMutation(
    async ({ barcode, amount }) => {
      await axios.patch(`http://127.0.0.1:8000/increase/${barcode}`, { amount });
    },
    {
      onSuccess: () => queryClient.invalidateQueries("ingredients"),
    }
  );

  const decreaseStock = useMutation(
    async ({ barcode, amount }) => {
      await axios.patch(`http://127.0.0.1:8000/decrease/${barcode}`, { amount });
    },
    {
      onSuccess: () => queryClient.invalidateQueries("ingredients"),
    }
  );

  const handleIncrease = (barcode) => {
    const amount = quantities[barcode] || 1;
    updateStock.mutate({ barcode, amount });
  };

  const handleDecrease = (barcode) => {
    const amount = quantities[barcode] || 1;
    decreaseStock.mutate({ barcode, amount });
  };

  const handleQuantityChange = (barcode, value) => {
    setQuantities({ ...quantities, [barcode]: Number(value) });
  };

  const handleExportCSV = async () => {
    const { data } = await axios.get("http://127.0.0.1:8000/export-csv");
    const blob = new Blob([data], { type: "text/csv" });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement("a");
    a.href = url;
    a.download = "ingredients.csv";
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);
  };

  if (isLoading) return <p>Loading ingredients...</p>;
  if (error) return <p>Error fetching ingredients</p>;

  return (
    <div>
      <div className="tabs">
        <button className="tab active">Table</button>
        <button className="tab">Expiration</button>
      </div>

      <button onClick={handleExportCSV} className="export-btn">Export to CSV</button>

      <table className="table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Quantity</th>
            <th>Container</th>
            <th>Storage Location</th>
            <th>Brand</th>
            <th>Category</th>
            <th>Actions</th>
            <th>Edit</th>
          </tr>
        </thead>
        <tbody>
          {ingredients.map((ingredient) => (
            <tr key={ingredient.barcode}>
              <td>{ingredient.ingredient_name}</td>
              <td>{ingredient.num_containers}</td>
              <td>{ingredient.container_type}</td>
              <td>{ingredient.storage_location}</td>
              <td>{ingredient.brand}</td>
              <td>{ingredient.item_category}</td>
              <td>
                <input
                  type="number"
                  min="1"
                  value={quantities[ingredient.barcode] || ""}
                  onChange={(e) => handleQuantityChange(ingredient.barcode, e.target.value)}
                  placeholder="Qty"
                  className="qty-input"
                />
                <button onClick={() => handleIncrease(ingredient.barcode)} className="increase-btn">+</button>
                <button onClick={() => handleDecrease(ingredient.barcode)} className="decrease-btn">-</button>
              </td>
              <td>
                <Link to={`/ingredient/${ingredient.barcode}`} className="edit-link">Edit</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default IngredientList;
