import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const IngredientDetails = () => {
  const { barcode } = useParams();
  const navigate = useNavigate();
  const [ingredient, setIngredient] = useState(null);
  const [formData, setFormData] = useState({});

  useEffect(() => {
    const fetchIngredient = async () => {
      const { data } = await axios.get(`http://127.0.0.1:8000/ingredient/${barcode}`);
      setIngredient(data);
      setFormData(data);
    };
    fetchIngredient();
  }, [barcode]);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSave = async () => {
    await axios.put(`http://127.0.0.1:8000/update-ingredient/${barcode}`, formData);
    navigate("/");
  };

  if (!ingredient) return <p>Loading...</p>;

  return (
    <div>
      <h2>Edit Ingredient</h2>
      <form>
        <label>Name: <input type="text" name="ingredient_name" value={formData.ingredient_name} onChange={handleChange} /></label>
        <label>Quantity: <input type="number" name="num_containers" value={formData.num_containers} onChange={handleChange} /></label>
        <label>Container: <input type="text" name="container_type" value={formData.container_type} onChange={handleChange} /></label>
        <label>Storage Location: <input type="text" name="storage_location" value={formData.storage_location} onChange={handleChange} /></label>
        <label>Brand: <input type="text" name="brand" value={formData.brand} onChange={handleChange} /></label>
        <label>Category: <input type="text" name="item_category" value={formData.item_category} onChange={handleChange} /></label>
        <button type="button" onClick={handleSave}>Save</button>
      </form>
    </div>
  );
};

export default IngredientDetails;
