import React, { useState } from "react";
import axios from "axios";

const StockControls = ({ ingredient, setIngredients }) => {
  const [amount, setAmount] = useState(1);

  const updateStock = async (action) => {
    if (amount <= 0) return;

    try {
      const url = `http://127.0.0.1:8000/${action}/${ingredient.barcode}`;
      const response = await axios.patch(url, { amount: parseFloat(amount) });

      if (response.status === 200) {
        // ✅ Update the state immediately without waiting for a full refresh
        setIngredients((prevIngredients) =>
          prevIngredients.map((ing) =>
            ing.barcode === ingredient.barcode
              ? { ...ing, num_containers: ing.num_containers + (action === "increase" ? amount : -amount) }
              : ing
          )
        );
      }
    } catch (error) {
      console.error(`Error updating stock (${action}):`, error);
    }
  };

  return (
    <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
      <input
        type="number"
        value={amount}
        min="1"
        onChange={(e) => setAmount(parseInt(e.target.value) || 1)}
        style={{ width: "50px", textAlign: "center" }}
      />
      <button onClick={() => updateStock("increase")} style={{ backgroundColor: "green", color: "white" }}>
        +
      </button>
      <button onClick={() => updateStock("decrease")} style={{ backgroundColor: "red", color: "white" }}>
        -
      </button>
    </div>
  );
};

export default StockControls;
