import React, { useState, useEffect } from "react";
import axios from "axios";
import "./MenuSubstitutions.css"; // Add styling

const daysOfWeek = ["Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday", "Sunday"];

const MenuSubstitutions = () => {
  const [ingredients, setIngredients] = useState([]);
  const [menu, setMenu] = useState({});

  useEffect(() => {
    fetchIngredients();
  }, []);

  const fetchIngredients = async () => {
    try {
      const response = await axios.get("http://127.0.0.1:8000/ingredients");
      if (response.data.ingredients && Array.isArray(response.data.ingredients)) {
        setIngredients(response.data.ingredients);
      } else {
        console.error("Invalid data format:", response.data);
      }
    } catch (error) {
      console.error("Error fetching ingredients:", error);
    }
  };

  const handleIngredientSelect = (day, mealType, ingredientType, value) => {
    setMenu((prevMenu) => ({
      ...prevMenu,
      [day]: {
        ...prevMenu[day],
        [mealType]: {
          ...prevMenu[day]?.[mealType],
          [ingredientType]: value,
        },
      },
    }));
  };

  return (
    <div className="menu-sub-container">
      <h2>Menu Substitutions</h2>
      <p>Select ingredients for each day's menu and check if they are feasible.</p>

      <div className="menu-grid">
        {daysOfWeek.map((day) => (
          <div key={day} className="day-column">
            <h3>{day}</h3>

            {/* Hot Meal Selection */}
            <div className="meal-section">
              <h4>Hot Meal</h4>
              <label>Protein:</label>
              <select onChange={(e) => handleIngredientSelect(day, "hot_meal", "protein", e.target.value)}>
                <option value="">Select</option>
                {ingredients.map((ing) => (
                  <option key={ing.barcode} value={ing.ingredient_name}>
                    {ing.ingredient_name}
                  </option>
                ))}
              </select>

              <label>Vegetable:</label>
              <select onChange={(e) => handleIngredientSelect(day, "hot_meal", "vegetable", e.target.value)}>
                <option value="">Select</option>
                {ingredients.map((ing) => (
                  <option key={ing.barcode} value={ing.ingredient_name}>
                    {ing.ingredient_name}
                  </option>
                ))}
              </select>

              <label>Starch:</label>
              <select onChange={(e) => handleIngredientSelect(day, "hot_meal", "starch", e.target.value)}>
                <option value="">Select</option>
                {ingredients.map((ing) => (
                  <option key={ing.barcode} value={ing.ingredient_name}>
                    {ing.ingredient_name}
                  </option>
                ))}
              </select>
            </div>

            {/* Cold Meal Selection */}
            <div className="meal-section">
              <h4>Cold Meal</h4>
              <label>Dessert:</label>
              <select onChange={(e) => handleIngredientSelect(day, "cold_meal", "dessert", e.target.value)}>
                <option value="">Select</option>
                {ingredients.map((ing) => (
                  <option key={ing.barcode} value={ing.ingredient_name}>
                    {ing.ingredient_name}
                  </option>
                ))}
              </select>

              <label>Vegetable:</label>
              <select onChange={(e) => handleIngredientSelect(day, "cold_meal", "vegetable", e.target.value)}>
                <option value="">Select</option>
                {ingredients.map((ing) => (
                  <option key={ing.barcode} value={ing.ingredient_name}>
                    {ing.ingredient_name}
                  </option>
                ))}
              </select>
            </div>
          </div>
        ))}
      </div>

      <button className="check-feasibility-btn">Check Feasibility</button>

      {/* Placeholder for Substitution Suggestions */}
      <div className="substitution-section">
        <h3>Substitution Suggestions</h3>
        <p>(This will display substitution recommendations once implemented.)</p>
      </div>
    </div>
  );
};

export default MenuSubstitutions;
