import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const IngredientList = () => {
  const [ingredients, setIngredients] = useState([]);
  const [filter, setFilter] = useState({ category: "", storage_location: "" });
  const [categories, setCategories] = useState([]);
  const [storageLocations, setStorageLocations] = useState([]);

  useEffect(() => {
    fetchIngredients();
  }, []);

  const fetchIngredients = async () => {
    try {
      const { data } = await axios.get("http://127.0.0.1:8000/ingredients");
      setIngredients(data.ingredients);

      // Extract distinct categories & storage locations
      setCategories([...new Set(data.ingredients.map((item) => item.item_category))]);
      setStorageLocations([...new Set(data.ingredients.map((item) => item.storage_location))]);
    } catch (error) {
      console.error("Error fetching ingredients:", error);
    }
  };

  const updateStock = async (barcode, amount, type) => {
    try {
      if (amount <= 0) return;

      await axios.patch(`http://127.0.0.1:8000/${type}/${barcode}`, { amount });

      // Keep the order stable
      setIngredients((prev) =>
        prev.map((item) =>
          item.barcode === barcode ? { ...item, num_containers: item.num_containers + (type === "increase" ? amount : -amount) } : item
        )
      );
    } catch (error) {
      console.error("Error updating stock:", error);
    }
  };

  const exportToCSV = () => {
    const csvContent = [
      ["Name", "Brand", "Quantity", "Container", "Storage Location"],
      ...ingredients.map(({ ingredient_name, brand, num_containers, container_type, storage_location }) => [
        ingredient_name,
        brand,
        num_containers,
        container_type,
        storage_location,
      ]),
    ]
      .map((row) => row.join(","))
      .join("\n");

    const blob = new Blob([csvContent], { type: "text/csv" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "inventory.csv";
    link.click();
  };

  const handleFilterChange = (e) => {
    setFilter({ ...filter, [e.target.name]: e.target.value });
  };

  const filteredIngredients = ingredients.filter((item) => {
    return (
      (filter.category ? item.item_category === filter.category : true) &&
      (filter.storage_location ? item.storage_location === filter.storage_location : true)
    );
  });

  return (
    <div className="max-w-7xl mx-auto p-6">
      {/* 🔹 Top Navigation Bar */}
      <div className="flex justify-between items-center border-b pb-4 mb-4">
        <div className="flex space-x-6">
          <button className="px-4 py-2 bg-blue-600 text-white font-semibold rounded-md hover:bg-blue-700">Inventory</button>
          <button className="px-4 py-2 bg-gray-200 text-gray-700 font-semibold rounded-md hover:bg-gray-300">Expiration</button>
        </div>
        <button
          className="px-4 py-2 bg-green-500 text-white font-semibold rounded-md hover:bg-green-600"
          onClick={exportToCSV}
        >
          Export to CSV
        </button>
      </div>

      {/* 🔹 Filter Section */}
      <div className="flex space-x-4 mb-4">
        <select
          name="category"
          className="border rounded-md p-2 bg-white shadow-sm"
          onChange={handleFilterChange}
        >
          <option value="">All Categories</option>
          {categories.map((cat) => (
            <option key={cat} value={cat}>
              {cat}
            </option>
          ))}
        </select>

        <select
          name="storage_location"
          className="border rounded-md p-2 bg-white shadow-sm"
          onChange={handleFilterChange}
        >
          <option value="">All Storage</option>
          {storageLocations.map((loc) => (
            <option key={loc} value={loc}>
              {loc}
            </option>
          ))}
        </select>
      </div>

      {/* 🔹 Styled Table */}
      <div className="overflow-x-auto bg-white shadow-md rounded-lg">
        <table className="w-full border-collapse">
          <thead className="bg-gray-100 text-gray-700 uppercase text-sm">
            <tr>
              <th className="p-3">Name</th>
              <th className="p-3">Brand</th>
              <th className="p-3">Quantity</th>
              <th className="p-3">Container</th>
              <th className="p-3">Storage Location</th>
              <th className="p-3">Actions</th>
              <th className="p-3">Edit</th>
            </tr>
          </thead>
          <tbody>
            {filteredIngredients.map((item, index) => (
              <tr key={item.barcode} className={index % 2 === 0 ? "bg-gray-50" : "bg-white"}>
                <td className="p-3 border">{item.ingredient_name}</td>
                <td className="p-3 border">{item.brand}</td>
                <td className="p-3 border">{item.num_containers}</td>
                <td className="p-3 border">{item.container_type}</td>
                <td className="p-3 border">{item.storage_location}</td>
                <td className="p-3 border">
                  <div className="flex space-x-2 items-center">
                    <input type="number" min="1" placeholder="Qty" id={`qty-${item.barcode}`} className="border p-1 rounded-md w-16" />
                    <button
                      className="px-3 py-1 bg-blue-500 text-white rounded-md hover:bg-blue-600"
                      onClick={() => updateStock(item.barcode, Number(document.getElementById(`qty-${item.barcode}`).value), "increase")}
                    >
                      +
                    </button>
                    <button
                      className="px-3 py-1 bg-red-500 text-white rounded-md hover:bg-red-600"
                      onClick={() => updateStock(item.barcode, Number(document.getElementById(`qty-${item.barcode}`).value), "decrease")}
                    >
                      -
                    </button>
                  </div>
                </td>
                <td className="p-3 border">
                  <Link to={`/ingredient/${item.barcode}`} className="text-blue-500 hover:underline">
                    Edit
                  </Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default IngredientList;
