import React from "react";
import { useMutation, useQueryClient } from "react-query";
import { updateStock } from "../supabaseClient";

const StockControls = ({ barcode }) => {
  const queryClient = useQueryClient();

  const updateMutation = useMutation(({ barcode, amount, type }) => updateStock(barcode, amount, type), {
    onSuccess: () => queryClient.invalidateQueries("ingredients"),
  });

  return (
    <div className="flex gap-2">
      <button
        onClick={() => updateMutation.mutate({ barcode, amount: 1, type: "increase" })}
        className="bg-green-500 text-white px-3 py-1 rounded"
      >
        ➕ Increase
      </button>
      <button
        onClick={() => updateMutation.mutate({ barcode, amount: 1, type: "decrease" })}
        className="bg-yellow-500 text-white px-3 py-1 rounded"
      >
        ➖ Decrease
      </button>
    </div>
  );
};

export default StockControls;
