import React, { useState } from "react";
import axios from "axios";

const StockControls = ({ ingredient, refreshData }) => {
  const [amount, setAmount] = useState(1);

  const handleIncrease = async () => {
    if (amount <= 0) return;
    try {
      console.log(`✅ Increasing stock by ${amount} for ${ingredient.barcode}`);
      const response = await axios.patch(`http://127.0.0.1:8000/increase/${ingredient.barcode}`, {
        amount: parseInt(amount), // Ensure it's an integer
      });
      console.log("✅ API Response:", response.data);
      await refreshData(); // ✅ Refresh UI after updating the database
    } catch (error) {
      console.error("❌ Error increasing stock:", error.response?.data || error);
    }
  };

  const handleDecrease = async () => {
    if (amount <= 0) return;
    try {
      console.log(`✅ Decreasing stock by ${amount} for ${ingredient.barcode}`);
      const response = await axios.patch(`http://127.0.0.1:8000/decrease/${ingredient.barcode}`, {
        amount: parseInt(amount), // Ensure it's an integer
      });
      console.log("✅ API Response:", response.data);
      await refreshData(); // ✅ Refresh UI after updating the database
    } catch (error) {
      console.error("❌ Error decreasing stock:", error.response?.data || error);
    }
  };

  return (
    <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
      <input
        type="number"
        value={amount}
        min="1"
        onChange={(e) => setAmount(Number(e.target.value) || 1)} // ✅ Ensure it's a valid number
        style={{ width: "50px", textAlign: "center" }}
      />
      <button onClick={handleIncrease} style={{ backgroundColor: "green", color: "white" }}>
        +
      </button>
      <button onClick={handleDecrease} style={{ backgroundColor: "red", color: "white" }}>
        -
      </button>
    </div>
  );
};

export default StockControls;
